// Very small vanilla JS UI for managing test prompts.
// Assumes a backend API exposed at /api/prompts

document.addEventListener('DOMContentLoaded', () => {
  const listEl = document.getElementById('promptList');
  const nameEl = document.getElementById('name');
  const templateEl = document.getElementById('template');
  const varsEl = document.getElementById('vars');
  const saveBtn = document.getElementById('saveBtn');
  const deleteBtn = document.getElementById('deleteBtn');
  const previewEl = document.getElementById('preview');

  let prompts = [];
  let currentId = null;

  function loadPrompts() {
    fetch('/api/prompts')
      .then(res => res.json())
      .then(data => {
        prompts = data || [];
        renderList();
      })
      .catch(() => { prompts = []; renderList(); });
  }

  function renderList() {
    listEl.innerHTML = '';
    prompts.forEach(p => {
      const li = document.createElement('li');
      li.textContent = p.name || '(unnamed)';
      li.className = 'item';
      li.onclick = () => loadPromptIntoEditor(p);
      listEl.appendChild(li);
    });
  }

  function loadPromptIntoEditor(p) {
    currentId = p.id;
    nameEl.value = p.name || '';
    templateEl.value = p.template || p.content || '';
    varsEl.value = JSON.stringify(p.variables || [], null, 2);
    renderPreview();
  }

  function renderPreview() {
    const name = nameEl.value;
    let tmpl = templateEl.value || '';
    // Gather variables and defaults
    let vars = [];
    try { vars = JSON.parse(varsEl.value); } catch (e) { vars = []; }
    const data = {};
    vars.forEach(v => { data[v.name] = v.default || ''; });
    // Simple mustache-like replacement: {{name}}
    const re = /\{\{\s*(\w+)\s*\}\}/g;
    const rendered = tmpl.replace(re, (m, key) => (data[key] !== undefined ? data[key] : m));
    previewEl.textContent = rendered;
  }

  saveBtn.onclick = () => {
    const payload = {
      name: nameEl.value,
      template: templateEl.value,
      variables: (() => {
        try { return JSON.parse(varsEl.value); } catch { return []; }
      })()
    };
    if (currentId) {
      fetch(`/api/prompts/${currentId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      }).then(() => loadPrompts())
        .catch(() => alert('Failed to save prompt'));
    } else {
      fetch('/api/prompts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      }).then(() => { loadPrompts(); currentId = null; clearEditor(); })
        .catch(() => alert('Failed to create prompt'));
    }
  };

  deleteBtn.onclick = () => {
    if (!currentId) return;
    fetch(`/api/prompts/${currentId}`, { method: 'DELETE' })
      .then(() => { loadPrompts(); clearEditor(); })
      .catch(() => alert('Failed to delete'));
  };

  nameEl.addEventListener('input', renderPreview);
  templateEl.addEventListener('input', renderPreview);
  varsEl.addEventListener('input', renderPreview);

  function clearEditor() {
    currentId = null;
    nameEl.value = '';
    templateEl.value = '';
    varsEl.value = '[]';
    previewEl.textContent = '';
  }

  // Initial load
  loadPrompts();
});

* { box-sizing: border-box; }
html, body { height: 100%; font-family: Arial, sans-serif; margin: 0; }
.app { display: flex; flex-direction: column; min-height: 100%; }
header { padding: 1rem; background: #2e3b55; color: white; }
.content { display: grid; grid-template-columns: 280px 1fr; gap: 1rem; padding: 1rem; }
.sidebar { border-right: 1px solid #ddd; padding-right: 1rem; }
.btn { padding: 0.5rem 1rem; margin: 0.25rem; border: none; background: #4a90e2; color: white; border-radius: 4px; cursor: pointer; }
.btn.danger { background: #e74c3c; }
.list { list-style: none; padding: 0; margin: 0; }
.item { padding: 0.5rem; border-bottom: 1px solid #eee; cursor: pointer; }
.editor label { display: block; margin: 0.5rem 0; }
.editor input, .editor textarea { width: 100%; padding: 0.5rem; border: 1px solid #ccc; border-radius: 4px; }
.editor .actions { margin-top: 0.5rem; }
.preview { background: #f6f6f6; padding: 0.75rem; border: 1px solid #ddd; border-radius: 4px; min-height: 120px; }
@media (max-width: 900px) { .content { grid-template-columns: 1fr; } }

// Lightweight backend to manage test prompts for the UI at /ui/prompts_ui
// Endpoints:
// - GET /api/prompts
// - POST /api/prompts
// - PUT /api/prompts/:id
// - DELETE /api/prompts/:id

const express = require('express');
const fs = require('fs');
const path = require('path');

const app = express();
app.use(express.json());

// Simple in-project data store (JSON file)
const DATA_DIR = path.join(__dirname, 'data');
const DATA_FILE = path.join(DATA_DIR, 'prompts.json');

// Ensure data dir exists
if (!fs.existsSync(DATA_DIR)) {
  fs.mkdirSync(DATA_DIR, { recursive: true });
}

function readPrompts() {
  try {
    const raw = fs.readFileSync(DATA_FILE, 'utf8');
    return JSON.parse(raw || '[]');
  } catch (e) {
    return [];
  }
}

function writePrompts(items) {
  fs.writeFileSync(DATA_FILE, JSON.stringify(items, null, 2));
}

// Basic CRUD
app.get('/api/prompts', (req, res) => {
  res.json(readPrompts());
});

app.post('/api/prompts', (req, res) => {
  const data = req.body || {};
  const items = readPrompts();
  const newItem = {
    id: Date.now().toString(),
    name: data.name || 'Untitled Prompt',
    template: data.template || '',
    variables: data.variables || []
  };
  items.push(newItem);
  writePrompts(items);
  res.json(newItem);
});

app.put('/api/prompts/:id', (req, res) => {
  const id = req.params.id;
  const data = req.body || {};
  let items = readPrompts();
  items = items.map(it => (it.id === id ? { ...it, ...data } : it));
  writePrompts(items);
  res.json(items.find(it => it.id === id));
});

app.delete('/api/prompts/:id', (req, res) => {
  const id = req.params.id;
  let items = readPrompts();
  items = items.filter(it => it.id !== id);
  writePrompts(items);
  res.json({ ok: true });
});

// Optional: serve UI static assets if needed
app.use('/ui', express.static(path.join(__dirname, '../ui/prompts_ui')));

const PORT = process.env.PORT || 3000;
app.listen(PORT, () => {
  // eslint-disable-next-line no-console
  console.log(`Test Prompt API server listening on port ${PORT}`);
});
