/* Copy-to-clipboard button inside message bubbles */
.msg { position: relative; padding-right: 30px; }
.copy-bubble-btn {
  position: absolute;
  top: 6px;
  right: 6px;
  background: #3e3e42;
  color: #e8e8e8;
  border: 1px solid #555;
  border-radius: 6px;
  padding: 2px 6px;
  font-size: 12px;
  cursor: pointer;
  opacity: 0.7;
  transition: opacity .15s ease, filter .15s ease;
}
.copy-bubble-btn:hover { opacity: 1; filter: brightness(1.05); }
.copy-bubble-btn:focus-visible { outline: none; box-shadow: var(--focus); }

