// Simple POSIX join to avoid pulling in 'path'
const joinPosix = (a, b) => (a.endsWith('/') ? a.slice(0, -1) : a) + '/' + b;

function getSftp(client) {
  return new Promise((resolve, reject) => {
    client.sftp((err, sftp) => (err ? reject(err) : resolve(sftp)));
  });
}

function readdirAsync(sftp, p) {
  return new Promise((resolve, reject) => {
    sftp.readdir(p, (err, list) => (err ? reject(err) : resolve(list || [])));
  });
}

export async function listTree(client, rootPath, depth = 2) {
  const maxDepth = Math.max(0, Math.min(5, depth));
  const sftp = await getSftp(client);

  async function walk(path, d) {
    const out = {};
    let entries = [];
    try {
      entries = await readdirAsync(sftp, path);
    } catch (e) {
      // If directory cannot be read, return empty
      return out;
    }
    for (const e of entries) {
      const name = e.filename;
      if (!name || name === '.' || name === '..') continue;
      try {
        if (e.attrs?.isDirectory?.()) {
          if (d > 0) {
            out[name] = { type: 'folder', children: await walk(joinPosix(path, name), d - 1) };
          } else {
            out[name] = { type: 'folder', children: {} };
          }
        } else if (e.attrs?.isFile?.()) {
          out[name] = { type: 'file', size: Number(e.attrs?.size || 0), mtime: Number(e.attrs?.mtime || 0) };
        } else {
          // Skip symlinks and special for now
        }
      } catch {
        // Skip problematic entries
      }
    }
    return out;
  }

  return await walk(rootPath || '/', maxDepth);
}

export async function readFileContent(client, remotePath) {
  const sftp = await getSftp(client);
  return new Promise((resolve, reject) => {
    const chunks = [];
    try {
      const rs = sftp.createReadStream(remotePath, { encoding: 'utf8' });
      rs.on('data', (c) => chunks.push(c));
      rs.on('error', (e) => reject(new Error(e?.message || 'SFTP read error')));
      rs.on('end', () => resolve(chunks.join('')));
    } catch (e) {
      reject(new Error(e?.message || 'SFTP read error'));
    }
  });
}

export async function listRemote(sessionId, path, depth = 2) {
  const s = sessions.get(sessionId);
  if (!s) throw new Error('Invalid session');
  return await listTree(s.client, path, depth);
}

export async function readRemote(sessionId, path) {
  const s = sessions.get(sessionId);
  if (!s) throw new Error('Invalid session');
  return await readFileContent(s.client, path);
}

app.get('/ssh/list', async (req, res) => {
  try {
    const sessionId = req.query.sessionId;
    const path = req.query.path;
    const depth = Math.max(0, Math.min(5, parseInt(req.query.depth || '2', 10)));
    if (!sessionId || !path) return res.status(400).json({ ok: false, error: 'Missing sessionId or path' });
    const tree = await listRemote(sessionId, path, depth);
    return res.json({ ok: true, tree });
  } catch (err) {
    return res.status(500).json({ ok: false, error: err?.message || 'List failed' });
  }
});

app.get('/ssh/read', async (req, res) => {
  try {
    const sessionId = req.query.sessionId;
    const path = req.query.path;
    if (!sessionId || !path) return res.status(400).json({ ok: false, error: 'Missing sessionId or path' });
    const content = await readRemote(sessionId, path);
    return res.json({ ok: true, content });
  } catch (err) {
    return res.status(500).json({ ok: false, error: err?.message || 'Read failed' });
  }
});

export async function fetchRemoteTree(remotePath, depth = 3) {
  if (!activeSessionId) throw new Error('No active session');
  const url = `${BACKEND_HTTP}/ssh/list?sessionId=${encodeURIComponent(activeSessionId)}&path=${encodeURIComponent(remotePath)}&depth=${depth}`;
  const res = await fetch(url);
  const data = await res.json();
  if (!data.ok) throw new Error(data.error || 'List failed');
  return data.tree || {};
}

export async function readRemoteFile(relPath) {
  if (!activeSessionId) throw new Error('No active session');
  const base = (state.remoteRoot || '').replace(/\/$/, '');
  const fullPath = base + (relPath.startsWith('/') ? relPath : '/' + relPath);
  const url = `${BACKEND_HTTP}/ssh/read?sessionId=${encodeURIComponent(activeSessionId)}&path=${encodeURIComponent(fullPath)}`;
  const res = await fetch(url);
  const data = await res.json();
  if (!data.ok) throw new Error(data.error || 'Read failed');
  return data.content ?? '';
}

